module Syntax.AbsJvmm where

-- Haskell module generated by the BNF converter


newtype Ident = Ident String deriving (Eq,Ord,Show)
newtype Semicolon = Semicolon ((Int,Int),String) deriving (Eq,Ord,Show)
newtype LeftBrace = LeftBrace ((Int,Int),String) deriving (Eq,Ord,Show)
newtype RightBrace = RightBrace ((Int,Int),String) deriving (Eq,Ord,Show)
data Program =
   Program [Definition]
  deriving (Eq,Ord,Show)

data Definition =
   DFunction Function
 | DClass Class
  deriving (Eq,Ord,Show)

data Function =
   Function TypeBasic Ident [Argument] Exceptions LeftBrace [Stmt] RightBrace
  deriving (Eq,Ord,Show)

data Argument =
   Argument TypeBasic Ident
  deriving (Eq,Ord,Show)

data Exceptions =
   NoExceptions
 | Exceptions [TypeComposed]
  deriving (Eq,Ord,Show)

data Class =
   Class Ident Extends LeftBrace [Member] RightBrace
  deriving (Eq,Ord,Show)

data Member =
   FieldsList TypeBasic [Field] Semicolon
 | Method Function
  deriving (Eq,Ord,Show)

data Field =
   Field Ident
  deriving (Eq,Ord,Show)

data Extends =
   SuperClass TypeComposed
 | SuperObject
  deriving (Eq,Ord,Show)

data TypeBasic =
   TComposed TypeComposed
 | TPrimitive TypePrimitive
  deriving (Eq,Ord,Show)

data TypeComposed =
   TObject
 | TUser Ident
 | TArray TypeBasic
 | TString
  deriving (Eq,Ord,Show)

data TypePrimitive =
   TInt
 | TChar
 | TBool
 | TVoid
  deriving (Eq,Ord,Show)

data Stmt =
   SThrow Expr Semicolon
 | STryCatch Stmt TypeComposed Ident Stmt
 | SBlock LeftBrace [Stmt] RightBrace
 | SEmpty Semicolon
 | SDeclVar TypeBasic [Item] Semicolon
 | SAssign Expr Expr Semicolon
 | SAssignOp Expr AssignOp Expr Semicolon
 | SPostInc Expr Semicolon
 | SPostDec Expr Semicolon
 | SReturn Expr Semicolon
 | SReturnV Semicolon
 | SIf Expr Stmt
 | SIfElse Expr Stmt Stmt
 | SWhile Expr Stmt
 | SForeach TypeBasic Ident Expr Stmt
 | SExpr Expr Semicolon
  deriving (Eq,Ord,Show)

data Item =
   NoInit Ident
 | Init Ident Expr
  deriving (Eq,Ord,Show)

data Expr =
   EVar Ident
 | ELitInt Integer
 | ELitTrue
 | ELitFalse
 | EString String
 | EThis
 | ENull
 | ENullT TypeComposed
 | ELitChar Char
 | EArray Expr Expr
 | EField Expr Ident
 | EMethod Expr Ident [Expr]
 | ENewObject TypeComposed
 | ENewArray TypeBasic Expr
 | EApp Ident [Expr]
 | ENeg Expr
 | ENot Expr
 | EMul Expr MulOp Expr
 | EAdd Expr AddOp Expr
 | ERel Expr RelOp Expr
 | EAnd Expr Expr
 | EOr Expr Expr
  deriving (Eq,Ord,Show)

data AssignOp =
   APlus
 | AMinus
 | ATimes
 | ADiv
 | AMod
  deriving (Eq,Ord,Show)

data AddOp =
   Plus
 | Minus
  deriving (Eq,Ord,Show)

data MulOp =
   Times
 | Div
 | Mod
  deriving (Eq,Ord,Show)

data RelOp =
   LTH
 | LEQ
 | GTH
 | GEQ
 | EQU
 | NEQ
  deriving (Eq,Ord,Show)

